var test=require("tape"),stringify=require("../");test("toJSON function",function(n){n.plan(1);var t={one:1,two:2,toJSON:function(){return{one:1}}};n.equal(stringify(t),'{"one":1}')}),test("toJSON returns string",function(n){n.plan(1);var t={one:1,two:2,toJSON:function(){return"one"}};n.equal(stringify(t),'"one"')}),test("toJSON returns array",function(n){n.plan(1);var t={one:1,two:2,toJSON:function(){return["one"]}};n.equal(stringify(t),'["one"]')});