function cleanup(){Person.remove(function(){mongoose.disconnect()})}var mongoose=require("../../lib");require("./person.js")();var Person=mongoose.model("Person");mongoose.connect("mongodb://localhost/persons",function(o){if(o)throw o;Person.create({name:"bill",age:25,birthday:(new Date).setFullYear((new Date).getFullYear()-25)},function(o,e){if(o)throw o;console.log("People added to db: %s",e.toString()),Person.find({},function(o,e){if(o)throw o;e.forEach(function(o){console.log("People in the db: %s",o.toString())}),setTimeout(function(){cleanup()},2e3)})})});