function cleanup(){Person.remove(function(){mongoose.disconnect()})}var async=require("async"),mongoose=require("../../lib");require("./person.js")();var Person=mongoose.model("Person"),data=[{name:"bill",age:25,birthday:(new Date).setFullYear((new Date).getFullYear()-25)},{name:"mary",age:30,birthday:(new Date).setFullYear((new Date).getFullYear()-30)},{name:"bob",age:21,birthday:(new Date).setFullYear((new Date).getFullYear()-21)},{name:"lilly",age:26,birthday:(new Date).setFullYear((new Date).getFullYear()-26)},{name:"alucard",age:1e3,birthday:(new Date).setFullYear((new Date).getFullYear()-1e3)}];mongoose.connect("mongodb://localhost/persons",function(e){if(e)throw e;async.each(data,function(e,n){Person.create(e,n)},function(e){var n=Person.find({age:{$lt:1e3}}).exec();n.addBack(function(){console.log("completed")}),n.addCallback(function(){console.log("Successful Completion!")}),n.addErrback(function(){console.log("Fail Boat")}),n.then(function(e){var n=e.map(function(e){return e._id});return Person.find({_id:{$nin:n}}).exec()}).then(function(e){console.log("Oldest person is: %s",e)}).then(cleanup)})});