function cleanup(){Person.remove(function(){mongoose.disconnect()})}var async=require("async"),mongoose=require("../../lib");require("./person.js")();var Person=mongoose.model("Person"),data=[{name:"bill",age:25,birthday:(new Date).setFullYear((new Date).getFullYear()-25)},{name:"mary",age:30,birthday:(new Date).setFullYear((new Date).getFullYear()-30)},{name:"bob",age:21,birthday:(new Date).setFullYear((new Date).getFullYear()-21)},{name:"lilly",age:26,birthday:(new Date).setFullYear((new Date).getFullYear()-26)},{name:"alucard",age:1e3,birthday:(new Date).setFullYear((new Date).getFullYear()-1e3)}];mongoose.connect("mongodb://localhost/persons",function(e){if(e)throw e;async.each(data,function(e,a){Person.create(e,a)},function(e){if(e)throw e;var a=Person.find({age:{$lt:1e3}});a.sort("birthday"),a.select("name"),a.where("age").gt(21),a.exec(function(e,a){if(e)throw e;console.log(a),cleanup()})})});